void setup() {
	//Start communications
	Serial.begin(9600);

	//Initialize the MAG3110
	mag.initialize();
	//Start calibration
	mag.enterCalMode();
}


// Global Variables 
int i = 0;
int animationTimer = millis();
BOOL calibrationDone = FALSE;

/* loop FUNCTION DESCRIPTION *******************************************
* SYNTAX:          void loop(void);
* KEYWORDS:        compass, sprite, calibration
* DESCRIPTION:     Initializes compass and compass sprites, displays compass
*				   sprites
* RETURN VALUE:    None
*
* END DESCRIPTION **********************************************************/
void loop() {

	//If we're in calibration mode
	if (mag.isCalibrating())
	{
		//Display the calibration screen
		if (millis() - animationTimer > 500)
		{
			i ^= 0x01; //Toggles on and off

			animationTimer = millis();

			mag.calibrate();
		}
	}


	//If we have just exited calibration mode
	if (mag.isCalibrated() && !calibrationDone)
	{
		printf("Exiting calibration mode \n\r");

		DelayMs(3000);
		calibrationDone = TRUE;
	}

	if (mag.isCalibrated())
	{
		//This used to cycle through all the compass sprites
		//if (i >11)
		//i = 0;

		//Make sure the top edge of the microview is pointed the same way as the x-axis of the MAG3110 unit
		//As in this case, you may need to add an offset to get the proper angle
		//In my circuit, the x-axis of the MAG3110 was 90 CW of the front of my microview, so I add 90 degrees
		//The other code makes the heading pertain to 0 to 360 degrees instead of +- 180
		int heading = (int)mag.readHeading() + 90;

		if (heading < 0) //If the heading is negative (ranges from -1 to -180)
			heading *= -1; //Negate the heading
		else if (heading > 0)
			heading = 360 - heading;
	}
}
